import React, { useState, useEffect } from "react";
import TaskInput from "./componentes/TaskInput";
import TaskList from "./componentes/TaskList";
import TaskFilter from "./componentes/TaskFilter";
import "./App.css";

function App() {
  const [tasks, setTasks] = useState(() => {
    const saved = localStorage.getItem("tasks");
    return saved ? JSON.parse(saved) : [];
  });

  const [filter, setFilter] = useState("all");
  const [search, setSearch] = useState("");
  const [message, setMessage] = useState("");
  const [dialog, setDialog] = useState(null); // controla o modal

  useEffect(() => {
    localStorage.setItem("tasks", JSON.stringify(tasks));
  }, [tasks]);

  const addTask = (text) => {
    if (!text.trim()) {
      setMessage("‚ö†Ô∏è Escreve algo antes de adicionar!");
      setTimeout(() => setMessage(""), 2000);
      return;
    }

    const newTask = { id: Date.now(), text, done: false };
    setTasks([...tasks, newTask]);
    setMessage("‚úÖ Tarefa adicionada!");
    setTimeout(() => setMessage(""), 1500);
  };

  const toggleTask = (id) =>
    setTasks(tasks.map((t) => (t.id === id ? { ...t, done: !t.done } : t)));

  const askDelete = (action, payload = null) => {
    // Abre o modal personalizado
    setDialog({ action, payload });
  };

  const confirmDelete = () => {
    if (!dialog) return;
    const { action, payload } = dialog;

    if (action === "single") {
      setTasks(tasks.filter((t) => t.id !== payload));
    } else if (action === "completed") {
      setTasks(tasks.filter((t) => !t.done));
    } else if (action === "all") {
      setTasks([]);
    }

    setDialog(null);
  };

  const cancelDelete = () => setDialog(null);

  const filteredTasks = tasks.filter((t) => {
    const matchFilter =
      filter === "completed" ? t.done : filter === "active" ? !t.done : true;
    const matchSearch = t.text.toLowerCase().includes(search.toLowerCase());
    return matchFilter && matchSearch;
  });

  const total = tasks.length;
  const completed = tasks.filter((t) => t.done).length;
  const active = total - completed;

  return (
    <div className="app-container">
      <h1>üß© To-Do List Barroca</h1>

      {message && <p className="message">{message}</p>}

      <TaskInput onAdd={addTask} />

      <TaskFilter
        filter={filter}
        setFilter={setFilter}
        search={search}
        setSearch={setSearch}
      />

      <div className="task-stats">
        <span>{active} ativas / {completed} conclu√≠das</span>
        <div className="action-buttons">
          {completed > 0 && (
            <button onClick={() => askDelete("completed")} className="clear-btn">
              Apagar conclu√≠das
            </button>
          )}
          {total > 0 && (
            <button onClick={() => askDelete("all")} className="clear-all-btn">
              Apagar tudo
            </button>
          )}
        </div>
      </div>

      <TaskList
        tasks={filteredTasks}
        onToggle={toggleTask}
        onDelete={(id) => askDelete("single", id)}
      />

      {/* Modal de confirma√ß√£o elegante */}
      {dialog && (
        <div className="dialog-overlay">
          <div className="dialog-box">
            <h3>‚ö†Ô∏è Confirma√ß√£o</h3>
            <p>
              {dialog.action === "single" && "Queres apagar esta tarefa?"}
              {dialog.action === "completed" && "Apagar todas as conclu√≠das?"}
              {dialog.action === "all" && "Tens a certeza que queres apagar TODAS as tarefas?"}
            </p>
            <div className="dialog-buttons">
              <button onClick={confirmDelete} className="confirm-btn">‚úÖ Confirmar</button>
              <button onClick={cancelDelete} className="cancel-btn">‚ùå Cancelar</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
