import React, { useState, useEffect } from "react";
import TaskInput from "./componentes/TaskInput";
import TaskList from "./componentes/TaskList";
import TaskFilter from "./componentes/TaskFilter";
import "./App.css";

function App() {
  // Estado principal das tarefas
  const [tasks, setTasks] = useState(() => {
    const saved = localStorage.getItem("tasks");
    return saved ? JSON.parse(saved) : [];
  });

  // Estado para filtros e pesquisa
  const [filter, setFilter] = useState("all");
  const [search, setSearch] = useState("");

  // Estado de feedback visual
  const [message, setMessage] = useState("");

  // Atualiza o localStorage sempre que as tarefas mudam
  useEffect(() => {
    localStorage.setItem("tasks", JSON.stringify(tasks));
  }, [tasks]);

  // Adiciona nova tarefa
  const addTask = (text) => {
    if (!text.trim()) {
      setMessage("‚ö†Ô∏è Escreve algo antes de adicionar!");
      setTimeout(() => setMessage(""), 2000);
      return;
    }

    const newTask = {
      id: Date.now(),
      text,
      done: false,
    };

    setTasks([...tasks, newTask]);
    setMessage("‚úÖ Tarefa adicionada com sucesso!");
    setTimeout(() => setMessage(""), 1500);
  };

  // Alterna entre conclu√≠do/n√£o conclu√≠do
  const toggleTask = (id) => {
    setTasks(tasks.map((t) =>
      t.id === id ? { ...t, done: !t.done } : t
    ));
  };

  // Elimina uma tarefa
  const deleteTask = (id) => {
    setTasks(tasks.filter((t) => t.id !== id));
  };

  // Elimina todas as conclu√≠das
  const clearCompleted = () => {
    const confirmDelete = window.confirm("Tens a certeza que queres apagar todas as conclu√≠das?");
    if (confirmDelete) {
      setTasks(tasks.filter((t) => !t.done));
    }
  };

  // Filtragem e pesquisa combinadas
  const filteredTasks = tasks.filter((t) => {
    const matchFilter =
      filter === "completed" ? t.done :
      filter === "active" ? !t.done : true;
    const matchSearch = t.text.toLowerCase().includes(search.toLowerCase());
    return matchFilter && matchSearch;
  });

  // Contadores
  const total = tasks.length;
  const completed = tasks.filter((t) => t.done).length;
  const active = total - completed;

  return (
    <div className="app-container">
      <h1>üß© To-Do List Inteligente</h1>

      {message && <p className="message">{message}</p>}

      <TaskInput onAdd={addTask} />

      <TaskFilter
        filter={filter}
        setFilter={setFilter}
        search={search}
        setSearch={setSearch}
      />

      <div className="task-stats">
        <span>{active} ativas / {completed} conclu√≠das</span>
        {completed > 0 && (
          <button onClick={clearCompleted} className="clear-btn">
            Apagar conclu√≠das
          </button>
        )}
      </div>

      <TaskList
        tasks={filteredTasks}
        onToggle={toggleTask}
        onDelete={deleteTask}
      />
    </div>
  );
}

export default App;
