import React, { useState, useEffect, useCallback } from "react";
import TaskInput from "./componentes/TaskInput";
import TaskList from "./componentes/TaskList";
import TaskFilter from "./componentes/TaskFilter";
import "./App.css";

export default function App() {
  const [tasks, setTasks] = useState(() => {
    try {
      return JSON.parse(localStorage.getItem("tasks")) || [];
    } catch {
      return [];
    }
  });
  const [filter, setFilter] = useState("all");
  const [search, setSearch] = useState("");
  const [message, setMessage] = useState("");
  const [dialog, setDialog] = useState(null);

  // guarda automaticamente
  useEffect(() => {
    localStorage.setItem("tasks", JSON.stringify(tasks));
  }, [tasks]);

  // fun√ß√£o utilit√°ria para mensagens
  const showMessage = useCallback((text, time = 1500) => {
    setMessage(text);
    setTimeout(() => setMessage(""), time);
  }, []);

  // adicionar tarefa
  const addTask = useCallback((text) => {
    if (!text.trim()) return showMessage("‚ö†Ô∏è Escreve algo antes de adicionar!", 2000);
    const newTask = { id: Date.now(), text: text.trim(), done: false };
    setTasks((prev) => [...prev, newTask]);
    showMessage("‚úÖ Tarefa adicionada!");
  }, [showMessage]);

  // alternar estado
  const toggleTask = useCallback((id) => {
    setTasks((prev) =>
      prev.map((t) => (t.id === id ? { ...t, done: !t.done } : t))
    );
  }, []);

  // confirmar elimina√ß√£o
  const confirmDelete = useCallback(() => {
    if (!dialog) return;
    const { action, payload } = dialog;
    setTasks((prev) => {
      if (action === "single") return prev.filter((t) => t.id !== payload);
      if (action === "completed") return prev.filter((t) => !t.done);
      if (action === "all") return [];
      return prev;
    });
    setDialog(null);
  }, [dialog]);

  const askDelete = (action, payload = null) => setDialog({ action, payload });
  const cancelDelete = () => setDialog(null);

  // filtragem + pesquisa
  const filteredTasks = tasks.filter(({ done, text }) => {
    if (filter === "active" && done) return false;
    if (filter === "completed" && !done) return false;
    return text.toLowerCase().includes(search.toLowerCase());
  });

  const total = tasks.length;
  const completed = tasks.filter((t) => t.done).length;
  const active = total - completed;

  return (
    <div className="app-container">
      <h1>üß© To-Do List Barroca</h1>
      {message && <p className="message">{message}</p>}

      <TaskInput onAdd={addTask} />
      <TaskFilter filter={filter} setFilter={setFilter} search={search} setSearch={setSearch} />

      <div className="task-stats">
        <span>{active} ativas / {completed} conclu√≠das</span>
        <div className="action-buttons">
          {completed > 0 && (
            <button onClick={() => askDelete("completed")} className="clear-btn">Apagar conclu√≠das</button>
          )}
          {total > 0 && (
            <button onClick={() => askDelete("all")} className="clear-all-btn">Apagar tudo</button>
          )}
        </div>
      </div>

      <TaskList tasks={filteredTasks} onToggle={toggleTask} onDelete={(id) => askDelete("single", id)} />

      {dialog && (
        <div className="dialog-overlay">
          <div className="dialog-box">
            <h3>‚ö†Ô∏è Confirma√ß√£o</h3>
            <p>
              {dialog.action === "single" && "Queres apagar esta tarefa?"}
              {dialog.action === "completed" && "Apagar todas as conclu√≠das?"}
              {dialog.action === "all" && "Tens a certeza que queres apagar TODAS as tarefas?"}
            </p>
            <div className="dialog-buttons">
              <button onClick={confirmDelete} className="confirm-btn">‚úÖ Confirmar</button>
              <button onClick={cancelDelete} className="cancel-btn">‚ùå Cancelar</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
