import React, { useState, useEffect } from "react";
import TaskInput from "./componentes/TaskInput";
import TaskList from "./componentes/TaskList";
import TaskFilter from "./componentes/TaskFilter";
import ConfirmDialog from "./componentes/ConfirmDialog";
import "./App.css";

function App() {
  const [tasks, setTasks] = useState(() => {
    const saved = localStorage.getItem("tasks");
    return saved ? JSON.parse(saved) : [];
  });
  const [filter, setFilter] = useState("all");
  const [search, setSearch] = useState("");
  const [message, setMessage] = useState("");
  const [dialog, setDialog] = useState(null);

  useEffect(() => {
    localStorage.setItem("tasks", JSON.stringify(tasks));
  }, [tasks]);

  const addTask = (text) => {
    if (!text.trim()) {
      setMessage("‚ö†Ô∏è Escreve algo antes de adicionar!");
      setTimeout(() => setMessage(""), 2000);
      return;
    }

    const newTask = { id: Date.now(), text, done: false };
    setTasks([...tasks, newTask]);
    setMessage("‚úÖ Tarefa adicionada!");
    setTimeout(() => setMessage(""), 1500);
  };

  const toggleTask = (id) =>
    setTasks(tasks.map((t) => (t.id === id ? { ...t, done: !t.done } : t)));

  const askDelete = (action, payload = null) => setDialog({ action, payload });
  const cancelDelete = () => setDialog(null);

  const confirmDelete = () => {
    if (!dialog) return;
    const { action, payload } = dialog;

    if (action === "single") {
      setTasks(tasks.filter((t) => t.id !== payload));
    } else if (action === "completed") {
      setTasks(tasks.filter((t) => !t.done));
    } else if (action === "all") {
      setTasks([]);
    }

    setDialog(null);
  };

  const filteredTasks = tasks.filter((t) => {
    const matchFilter =
      filter === "completed" ? t.done : filter === "active" ? !t.done : true;
    const matchSearch = t.text.toLowerCase().includes(search.toLowerCase());
    return matchFilter && matchSearch;
  });

  const total = tasks.length;
  const completed = tasks.filter((t) => t.done).length;
  const active = total - completed;

  // Define a mensagem que ser√° mostrada no modal
  const getDialogMessage = () => {
    if (!dialog) return "";
    if (dialog.action === "single") return "Queres apagar esta tarefa?";
    if (dialog.action === "completed") return "Apagar todas as conclu√≠das?";
    if (dialog.action === "all") return "Tens a certeza que queres apagar TODAS as tarefas?";
  };

  return (
    <div className="app-container">
      <h1>üß© To-Do List Barroca</h1>

      {message && <p className="message">{message}</p>}

      <TaskInput onAdd={addTask} />

      <TaskFilter
        filter={filter}
        setFilter={setFilter}
        search={search}
        setSearch={setSearch}
      />

      <div className="task-stats">
        <span>{active} ativas / {completed} conclu√≠das</span>
        <div className="action-buttons">
          {completed > 0 && (
            <button
              onClick={() => askDelete("completed")}
              className="clear-btn"
            >
              Apagar conclu√≠das
            </button>
          )}
          {total > 0 && (
            <button
              onClick={() => askDelete("all")}
              className="clear-all-btn"
            >
              Apagar tudo
            </button>
          )}
        </div>
      </div>

      <TaskList
        tasks={filteredTasks}
        onToggle={toggleTask}
        onDelete={(id) => askDelete("single", id)}
      />

      <ConfirmDialog
        visible={!!dialog}
        title="‚ö†Ô∏è Confirma√ß√£o"
        message={getDialogMessage()}
        onConfirm={confirmDelete}
        onCancel={cancelDelete}
      />
    </div>
  );
}

export default App;
