import React, { useState, useEffect } from "react";
import TaskInput from "./componentes/TaskInput";
import TaskList from "./componentes/TaskList";
import TaskFilter from "./componentes/TaskFilter";
import "./App.css";

function App() {
  const [tasks, setTasks] = useState(() => {
    const saved = localStorage.getItem("tasks");
    return saved ? JSON.parse(saved) : [];
  });

  const [filter, setFilter] = useState("all");
  const [search, setSearch] = useState("");
  const [message, setMessage] = useState("");

  useEffect(() => {
    localStorage.setItem("tasks", JSON.stringify(tasks));
  }, [tasks]);

  // ‚ûï Adicionar tarefa
  const addTask = (text) => {
    if (!text.trim()) {
      setMessage("‚ö†Ô∏è Escreve algo antes de adicionar!");
      setTimeout(() => setMessage(""), 2000);
      return;
    }

    const newTask = { id: Date.now(), text, done: false };
    setTasks([...tasks, newTask]);
    setMessage("‚úÖ Tarefa adicionada com sucesso!");
    setTimeout(() => setMessage(""), 1500);
  };

  // ‚úÖ Alternar estado
  const toggleTask = (id) =>
    setTasks(tasks.map((t) => (t.id === id ? { ...t, done: !t.done } : t)));

  // ‚ùå Apagar tarefa individual com confirma√ß√£o
  const deleteTask = (id) => {
    const task = tasks.find((t) => t.id === id);
    if (window.confirm(`Apagar a tarefa: "${task.text}" ?`)) {
      setTasks(tasks.filter((t) => t.id !== id));
    }
  };

  // üßΩ Apagar todas as conclu√≠das
  const clearCompleted = () => {
    const completed = tasks.filter((t) => t.done).length;
    if (completed === 0) {
      alert("N√£o h√° tarefas conclu√≠das para apagar.");
      return;
    }
    if (window.confirm(`Apagar ${completed} tarefa(s) conclu√≠da(s)?`)) {
      setTasks(tasks.filter((t) => !t.done));
    }
  };

  // üí£ Apagar toda a lista
  const clearAll = () => {
    if (tasks.length === 0) {
      alert("A lista j√° est√° vazia.");
      return;
    }
    if (window.confirm("Tens a certeza que queres apagar TODAS as tarefas?")) {
      setTasks([]);
      setMessage("üß® Todas as tarefas foram apagadas!");
      setTimeout(() => setMessage(""), 2000);
    }
  };

  // üîé Filtro + pesquisa
  const filteredTasks = tasks.filter((t) => {
    const matchFilter =
      filter === "completed" ? t.done : filter === "active" ? !t.done : true;
    const matchSearch = t.text.toLowerCase().includes(search.toLowerCase());
    return matchFilter && matchSearch;
  });

  // üìä Contadores
  const total = tasks.length;
  const completed = tasks.filter((t) => t.done).length;
  const active = total - completed;

  return (
    <div className="app-container">
      <h1>üß© To-Do List Inteligente</h1>

      {message && <p className="message">{message}</p>}

      <TaskInput onAdd={addTask} />

      <TaskFilter
        filter={filter}
        setFilter={setFilter}
        search={search}
        setSearch={setSearch}
      />

      {/* Estat√≠sticas e bot√µes de a√ß√£o */}
      <div className="task-stats">
        <span>{active} ativas / {completed} conclu√≠das</span>
        <div className="action-buttons">
          {completed > 0 && (
            <button onClick={clearCompleted} className="clear-btn">
              Apagar conclu√≠das
            </button>
          )}
          {total > 0 && (
            <button onClick={clearAll} className="clear-all-btn">
              Apagar tudo
            </button>
          )}
        </div>
      </div>

      <TaskList tasks={filteredTasks} onToggle={toggleTask} onDelete={deleteTask} />
    </div>
  );
}

export default App;
