import React, { useState, useEffect } from "react";
import TaskInput from "./componentes/TaskInput";
import TaskList from "./componentes/TaskList";
import TaskFilter from "./componentes/TaskFilter";
import "./App.css";

function App() {
  const [tasks, setTasks] = useState(() => {
    const saved = localStorage.getItem("tasks");
    return saved ? JSON.parse(saved) : [];
  });

  const [filter, setFilter] = useState("all");
  const [search, setSearch] = useState("");
  const [message, setMessage] = useState("");

  useEffect(() => {
    localStorage.setItem("tasks", JSON.stringify(tasks));
  }, [tasks]);

  const addTask = (text) => {
    if (!text.trim()) {
      setMessage("‚ö†Ô∏è Escreve algo antes de adicionar!");
      setTimeout(() => setMessage(""), 2000);
      return;
    }
    const newTask = { id: Date.now(), text, done: false };
    setTasks([...tasks, newTask]);
    setMessage("‚úÖ Tarefa adicionada!");
    setTimeout(() => setMessage(""), 1500);
  };

  const toggleTask = (id) =>
    setTasks(tasks.map((t) => (t.id === id ? { ...t, done: !t.done } : t)));

  const deleteTask = (id) => setTasks(tasks.filter((t) => t.id !== id));

  const clearCompleted = () => {
    if (window.confirm("Apagar todas as conclu√≠das?")) {
      setTasks(tasks.filter((t) => !t.done));
    }
  };

  const filteredTasks = tasks.filter((t) => {
    const matchFilter =
      filter === "completed" ? t.done : filter === "active" ? !t.done : true;
    const matchSearch = t.text.toLowerCase().includes(search.toLowerCase());
    return matchFilter && matchSearch;
  });

  const total = tasks.length;
  const completed = tasks.filter((t) => t.done).length;
  const active = total - completed;

  return (
    <div className="app-container">
      <h1>üß© To-Do List Barroca</h1>
      {message && <p className="message">{message}</p>}
      <TaskInput onAdd={addTask} />

      <TaskFilter
        filter={filter}
        setFilter={setFilter}
        search={search}
        setSearch={setSearch}
      />

      <div className="task-stats">
        <span>{active} ativas / {completed} conclu√≠das</span>
        {completed > 0 && (
          <button onClick={clearCompleted} className="clear-btn">
            Apagar conclu√≠das
          </button>
        )}
      </div>

      <div className="task-list-container">
        <TaskList
          tasks={filteredTasks}
          onToggle={toggleTask}
          onDelete={deleteTask}
        />
      </div>
    </div>
  );
}

export default App;
